import React from 'react';

interface Chat {
  name: string;
  message: string;
  time: string;
  avatar: string;
}

interface MessageSystemProps {
  chat: Chat | null;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  if (!chat) {
    return (
      <div className="flex-grow flex flex-col h-full justify-center items-center">
        <p>Select a chat to see messages.</p>
      </div>
    );
  }

  return (
    <div className="flex-grow flex flex-col h-full">
      <div className="flex justify-between items-center p-4 border-b">
        <div className="text-xl font-[700] text-[#232323] flex items-center space-x-2">
          <img className="w-10 h-10 rounded-full" src={chat.avatar} alt={chat.name} />
          <span>{chat.name}</span>
        </div>
      </div>
      <div className="flex flex-col flex-grow overflow-y-auto p-4">
        <div className="flex flex-row mb-4">
          <img
            src={chat.avatar}
            alt={chat.name}
            className="w-10 h-10 rounded-full"
          />
          <div className='ml-4'>
            <p className="bg-white p-2 text-xs rounded-lg shadow-md">
              {chat.message}
              <span className="text-xs text-gray-500 float-right">{chat.time}</span>
            </p>
          </div>
        </div>
        {/* Add a message input here */}
        <div className="flex items-center border-t p-4">
          <input
            type="text"
            placeholder="Type here"
            className="flex-grow p-2 border rounded-lg"
          />
          <button className="ml-2 bg-blue-500 text-white p-2 rounded-lg">Send</button>
        </div>
      </div>
    </div>
  );
};

export default MessageSystem;
