const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Chat[]>([]);
  const [file, setFile] = useState<File | null>(null);

  useEffect(() => {
    // Filter messages based on the selected chat
    if (chat) {
      setMessages(prevMessages => prevMessages.filter(msg =>
        msg.name === chat.name || msg.name === 'User'
      ));
    }
  }, [chat]);

  // Other methods remain unchanged

  if (!chat) {
    return (
      <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
        <div className="flex justify-between items-center mb-4">
          <div className="text-xl font-[700] mb-5 text-[#232323] flex items-center space-x-2">
            <span>Select a chat to see messages.</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-grow mb-4 flex flex-col h-[calc(100vh-5rem)]">
      {/* Header with chat information */}
      <div className="flex justify-between items-center mb-4">
        <div className="text-xl font-[700] mb-5 text-[#232323] flex items-center space-x-2">
          <span>{chat.name}</span>
        </div>
      </div>

      {/* Chat messages */}
      <div className="flex-grow overflow-y-auto mb-4">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`flex ${message.name === 'User' ? 'justify-end' : 'justify-start'} mb-2`}
          >
            <div className={`flex items-center ${message.name === 'User' ? 'bg-blue-100' : 'bg-gray-100'} p-2 rounded-lg`}>
              <span>{message.message}</span>
            </div>
          </div>
        ))}
      </div>

      {/* Message input */}
      <div className="flex items-center space-x-2 p-2 border-t">
        <input
          type="text"
          value={messageInput}
          onChange={(e) => setMessageInput(e.target.value)}
          className="flex-grow border rounded-lg p-2"
          placeholder="Type a message..."
        />
        <button
          onClick={handleSendMessage}
          className="bg-blue-500 text-white px-4 py-2 rounded-lg"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default MessageSystem;
