import React, { useState } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";

interface Message {
  name: string;
  message: string;
  time: string;
  avatar: string;
  status: string;
}

interface MessageSystemProps {
  chat: { name: string; avatar: string; status: string } | null;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [file, setFile] = useState<File | null>(null);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleSendMessage = () => {
    if (messageInput.trim()) {
      const now = new Date();
      const newMessage: Message = {
        name: 'Me', // Update with the user's name
        message: messageInput,
        time: `${now.getHours()}:${now.getMinutes()}`,
        avatar: '', // Update with user's avatar if available
        status: 'sent' // or 'delivered', 'read', etc.
      };
      setMessages([...messages, newMessage]);
      setMessageInput('');
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      setFile(e.target.files[0]);
    }
  };

  const handleSendFile = () => {
    if (file) {
      // Handle file upload
      console.log('File to upload:', file);
    }
  };

  return (
    <div className="flex flex-col h-full border-l border-gray-300">
      <div className="flex items-center p-4 bg-gray-100 border-b">
        <IoPersonCircleSharp className="w-9 h-9" />
        <div className="ml-3">
          <div className="text-xs font-semibold">{chat?.name}</div>
          <div className="text-xs text-gray-500">{chat?.status}</div>
        </div>
      </div>

      <div className="flex-1 p-4 overflow-y-auto">
        {messages.map((msg, index) => (
          <div key={index} className={`flex ${msg.name === 'Me' ? 'justify-end' : 'justify-start'}`}>
            <div className={`p-2 rounded-lg ${msg.name === 'Me' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>
              <div className="text-xs">{msg.message}</div>
              <div className="text-xs text-gray-500">{msg.time}</div>
            </div>
          </div>
        ))}
      </div>

      <div className="p-4 bg-gray-100 border-t flex items-center">
        <input
          type="file"
          onChange={handleFileChange}
          className="mr-2"
        />
        <button
          onClick={handleSendFile}
          className="bg-blue-500 text-white py-2 px-4 rounded"
        >
          Send File
        </button>
        <input
          type="text"
          value={messageInput}
          onChange={handleInputChange}
          placeholder="Type a message"
          className="flex-1 ml-2 p-2 border rounded"
        />
        <button
          onClick={handleSendMessage}
          className="bg-blue-500 text-white py-2 px-4 rounded"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default MessageSystem;
