import React, { useState, useEffect, useRef, KeyboardEvent } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";

interface Chat {
  name: string;
  avatar: string;
  status: string;
  mobileNumber: string;
}

interface Message {
  sender_mobile: string;
  receiver_mobile: string;
  sender_name: string;
  receiver_name: string;
  message: string;
  time: string;
}

interface MessageSystemProps {
  chat: Chat | null;
  currentUserMobile: string;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat, currentUserMobile }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const socketRef = useRef<WebSocket | null>(null);

  useEffect(() => {
    if (chat) {
      const fetchMessages = async () => {
        try {
          const response = await fetch(`/api/messages?sender_mobile=${currentUserMobile}&receiver_mobile=${chat.mobileNumber}`);
          const data = await response.json();
          setMessages(data);
        } catch (error) {
          console.error('Error fetching messages:', error);
        }
      };

      fetchMessages();
    }
  }, [chat, currentUserMobile]);

  useEffect(() => {
    // Connect to WebSocket server
    socketRef.current = new WebSocket(`ws://localhost:5000`);

    // Receive messages from the WebSocket server
    socketRef.current.onmessage = (event) => {
      const newMessage = JSON.parse(event.data);
      setMessages(prevMessages => [...prevMessages, newMessage]);
    };

    return () => {
      // Close the WebSocket connection when the component unmounts
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleSendMessage = async () => {
    if (messageInput.trim()) {
      const newMessage: Message = {
        sender_mobile: currentUserMobile,
        receiver_mobile: chat?.mobileNumber || '',
        sender_name: 'User', // Replace with actual sender name
        receiver_name: chat?.name || 'User',
        message: messageInput,
        time: new Date().toLocaleTimeString(),
      };

      try {
        await fetch('/api/messages', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newMessage),
        });

        // Send message through WebSocket
        if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
          socketRef.current.send(JSON.stringify(newMessage));
        }

        setMessageInput('');
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSendMessage();
    }
  };

  if (!chat) {
    return (
      <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
        <div className="flex justify-between items-center mb-4">
          <div className="text-xl font-[600] mb-5 text-[#232323] flex items-center space-x-2">
            <span>Select a chat to see messages.</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-grow mb-4 flex flex-col h-[calc(100vh-5rem)]">
      <div className="flex justify-between items-center mb-4">
        <div className="text-xl font-[700] text-[#232323] flex items-center space-x-2">
          <IoPersonCircleSharp className='w-14 h-14' />
          <div>
            <span className='font-medium'>{chat.name}</span>
            <div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
              {chat.status}
            </div>
          </div>
        </div>
      </div>

      <div className="flex-grow overflow-y-auto">
        <div className="flex flex-col space-y-4">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`flex ${message.sender_mobile === currentUserMobile ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`rounded-lg p-2 text-sm text-white ${
                  message.sender_mobile === currentUserMobile ? 'bg-blue-500' : 'bg-gray-500'
                }`}
              >
                {message.message}
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="mt-2">
        <input
          type="text"
          className="w-full p-2 border rounded-md"
          placeholder="Type a message"
          value={messageInput}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
        />
      </div>
    </div>
  );
};

export default MessageSystem;
