import React from 'react';

interface Chat {
  name: string;
  message: string;
  time: string;
  avatar: string;
  status: string; // Added status field
}

interface MessageSystemProps {
  chat: Chat | null;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  if (!chat) {
    return (
      <div className="flex-grow flex flex-col h-full justify-center items-center p-4">
        <p>Select a chat to see messages.</p>
      </div>
    );
  }

  return (
    <div className="flex-grow flex flex-col h-full">
      {/* Header with chat information */}
      <div className="flex flex-col p-4 border-b">
        <div className="flex items-center space-x-2">
          <img className="w-10 h-10 rounded-full" src={chat.avatar} alt={chat.name} />
          <div>
            <span className="text-xl font-[700] text-[#232323]">{chat.name}</span>
            <div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
              {chat.status}
            </div>
          </div>
        </div>
      </div>

      {/* Message area */}
      <div className="flex flex-col flex-grow overflow-y-auto p-4 bg-[#f6f6f8]">
        {/* Example message entries */}
        <div className="flex flex-row mb-4">
          <img src={chat.avatar} alt={chat.name} className="w-10 h-10 rounded-full" />
          <div className='ml-4'>
            <p className="bg-white p-2 text-xs rounded-lg shadow-md">
              {chat.message}
              <span className="text-xs text-gray-500 float-right">{chat.time}</span>
            </p>
          </div>
        </div>

        {/* Add more messages here as needed */}

      </div>

      {/* Input area */}
      <div className="flex items-center border-t p-4 bg-white">
        <input
          type="text"
          placeholder="Type here"
          className="flex-grow p-2 border rounded-lg"
        />
        <button className="ml-2 bg-blue-500 text-white p-2 rounded-lg">Send</button>
      </div>
    </div>
  );
};

export default MessageSystem;
