import React, { useState, useEffect, KeyboardEvent } from 'react';
import { IoPersonCircleSharp } from 'react-icons/io5';

interface Chat {
  name: string;
  avatar: string;
  status: string;
  mobileNumber: string;
}

interface Message {
  sender_mobile: string;
  receiver_mobile: string;
  sender_name: string;
  receiver_name: string;
  message: string;
  time: string;
}

interface MessageSystemProps {
  chat: Chat | null;
  senderMobile: string;
  senderName: string;// Mobile number of the logged-in user
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat, senderMobile, senderName }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [file, setFile] = useState<File | null>(null);
  const [loggedInUserName, setLoggedInUserName] = useState<string | null>(null);
  const [recipientName, setRecipientName] = useState<string | null>(null);

  useEffect(() => {
    if (loggedInUserMobile) {
      // Fetch logged-in user details
      const fetchLoggedInUser = async () => {
        try {
          const response = await fetch(`/api/user/${loggedInUserMobile}`);
          const data = await response.json();
          setLoggedInUserName(data.name);
        } catch (error) {
          console.error('Error fetching logged-in user:', error);
        }
      };

      fetchLoggedInUser();
    }
  }, [loggedInUserMobile]);

  useEffect(() => {
    if (chat) {
      const fetchMessages = async () => {
        try {
          const response = await fetch(`/api/messages?sender_mobile=${loggedInUserMobile}&receiver_mobile=${chat.mobileNumber}`);
          const data = await response.json();
          setMessages(data);
        } catch (error) {
          console.error('Error fetching messages:', error);
        }
      };

      const fetchRecipientName = async () => {
        try {
          const response = await fetch(`/api/user/${chat.mobileNumber}`);
          const data = await response.json();
          setRecipientName(data.name);
        } catch (error) {
          console.error('Error fetching recipient name:', error);
        }
      };

      fetchMessages();
      fetchRecipientName();
    }
  }, [chat, loggedInUserMobile]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleSendMessage = async () => {
    if (messageInput.trim() && loggedInUserName && recipientName) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage: Message = {
        sender_mobile: loggedInUserMobile,
        receiver_mobile: chat?.mobileNumber || '',
        sender_name: loggedInUserName,
        receiver_name: recipientName,
        message: messageInput,
        time: formattedTime,
      };

      try {
        await fetch('/api/messages', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newMessage),
        });
        setMessages([...messages, newMessage]);
        setMessageInput(''); // Clear input after sending
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files ? e.target.files[0] : null;
    if (selectedFile && loggedInUserName && recipientName) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage: Message = {
        sender_mobile: loggedInUserMobile,
        receiver_mobile: chat?.mobileNumber || '',
        sender_name: loggedInUserName,
        receiver_name: recipientName,
        message: `Sent a file: ${selectedFile.name}`,
        time: formattedTime,
      };

      try {
        await fetch('/api/messages', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newMessage),
        });
        setMessages([...messages, newMessage]);
        setFile(null); // Clear the file after sending
      } catch (error) {
        console.error('Error sending file message:', error);
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault(); // Prevent the default behavior of the Enter key
      handleSendMessage(); // Call the send message function
    }
  };

  if (!chat) {
    return (
      <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
        <div className="flex justify-between items-center mb-4">
          <div className="text-xl font-[600] mb-5 text-[#232323] flex items-center space-x-2">
            <span>Select a chat to see messages.</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-grow mb-4 flex flex-col h-[calc(100vh-5rem)]">
      {/* Header with chat information */}
      <div className="flex justify-between items-center mb-4">
        <div className="text-xl font-[700] text-[#232323] flex items-center space-x-2">
          <div><IoPersonCircleSharp className="w-14 h-14" /></div>
          <div>
            <span className="font-medium">{chat.name}</span>
            <div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
              {chat.status}
            </div>
          </div>
        </div>
      </div>

      {/* Message area */}
      <div className="flex flex-col flex-grow overflow-y-auto bg-[#f6f6f8] p-4">
        {/* Display messages */}
        {messages.map((msg, index) => (
          <div key={index} className={`flex ${msg.sender_name === loggedInUserName ? 'justify-end' : ''} mb-4`}>
            {msg.sender_name !== loggedInUserName && <img src={chat?.avatar || '/assets/default-avatar.svg'} alt={msg.sender_name} className="w-10 h-10 rounded-full mr-2" />}
            <div className={`flex flex-col ${msg.sender_name === loggedInUserName ? 'items-end' : ''}`}>
              <p className="bg-white p-2 w-[490px] text-xs rounded-lg shadow-md">
                {msg.message}
                <span className="text-xs text-gray-500 float-right" style={{ position: 'relative', top: '-5px', marginRight: '10px' }}>
                  {msg.time}
                </span>
              </p>
            </div>
            {msg.sender_name === loggedInUserName && <img src={chat?.avatar || '/assets/default-avatar.svg'} alt={msg.sender_name} className="w-10 h-10 rounded-full ml-2" />}
          </div>
        ))}
      </div>

      {/* Input area */}
      <div className="flex items-center mb-4 p-4 border-t bg-[#ffffff] shadow-md">
        <input
          type="file"
          id="file-input"
          className="hidden"
          onChange={handleFileChange}
        />
        <label htmlFor="file-input">
          <img className="p-2 mr-2 bg-white w-10 h-10 cursor-pointer" src="/assets/add.svg" alt="Add" />
        </label>
        <input
          type="text"
          value={messageInput}
          onChange={handleInputChange}
          placeholder="Type Here"
          onKeyDown={handleKeyDown} // Add key down event handler
          className="text-xs font-medium text-gray-400 flex-grow p-2 border rounded-lg outline-none" 
        />
        <img
          onClick={handleSendMessage}
          className="p-2 bg-white w-14 h-14 cursor-pointer"
          src="/assets/sendbutton.svg"
          alt="Send"
        />
      </div>
    </div>
  );
};

export default MessageSystem;
