import React, { useState, useEffect, useRef, KeyboardEvent } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";
import { io, Socket } from 'socket.io-client'; // Import Socket.IO client

interface Chat {
  name: string;
  avatar: string;
  status: string;
  mobileNumber: string;
}

interface Message {
  sender_mobile: string;
  receiver_mobile: string;
  sender_name: string;
  receiver_name: string;
  message: string;
  time: string;
}

interface MessageSystemProps {
  chat: Chat | null;
  currentUserMobile: string;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat, currentUserMobile }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [file, setFile] = useState<File | null>(null);
  const socketRef = useRef<Socket | null>(null);

  useEffect(() => {
    if (chat) {
      const fetchMessages = async () => {
        try {
          const response = await fetch(`/api/messages?sender_mobile=${currentUserMobile}&receiver_mobile=${chat.mobileNumber}`);
          const data = await response.json();
          setMessages(data);
        } catch (error) {
          console.error('Error fetching messages:', error);
        }
      };

      fetchMessages();
    }
  }, [chat, currentUserMobile]);

  useEffect(() => {
    if (chat) {
      // Initialize WebSocket connection
      socketRef.current = io('http://localhost:5000'); // Replace with your backend WebSocket URL

      // Listen for incoming messages
      socketRef.current.on('receiveMessage', (newMessage: Message) => {
        setMessages(prevMessages => [...prevMessages, newMessage]);
      });

      return () => {
        socketRef.current?.disconnect();
      };
    }
  }, [chat]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleSendMessage = async () => {
    if (messageInput.trim()) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',       // Include seconds in the formatted time
        hour12: true,            // Use 12-hour format with AM/PM
        timeZone: 'Asia/Kolkata' // Set the time zone to IST (Indian Standard Time)
      };
      const formattedTime = now.toLocaleTimeString('en-US', options);

      const newMessage: Message = {
        sender_mobile: currentUserMobile,
        receiver_mobile: chat?.mobileNumber || '',
        sender_name: 'User', // Replace with actual sender name
        receiver_name: chat?.name || 'User',
        message: messageInput,
        time: formattedTime,
      };

      try {
        // Send message through WebSocket
        socketRef.current?.emit('sendMessage', newMessage);

        // Optionally, also save the message in the backend
        await fetch('/api/messages', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newMessage),
        });

        setMessages([...messages, newMessage]);
        setMessageInput('');
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files ? e.target.files[0] : null;
    if (selectedFile) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage: Message = {
        sender_mobile: currentUserMobile,
        receiver_mobile: chat?.mobileNumber || '',
        sender_name: 'User', // Replace with actual sender name
        receiver_name: chat?.name || 'User',
        message: `Sent a file: ${selectedFile.name}`,
        time: formattedTime,
      };

      try {
        // Send file message through WebSocket
        socketRef.current?.emit('sendMessage', newMessage);

        // Optionally, also save the message in the backend
        await fetch('/api/messages', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newMessage),
        });

        setMessages([...messages, newMessage]);
        setFile(null);
      } catch (error) {
        console.error('Error sending file message:', error);
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSendMessage();
    }
  };

  if (!chat) {
    return (
      <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
        <div className="flex justify-between items-center mb-4">
          <div className="text-xl font-[600] mb-5 text-[#232323] flex items-center space-x-2">
            <span>Select a chat to see messages.</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-grow mb-4 flex flex-col h-[calc(100vh-5rem)]">
      {/* Header with chat information */}
      <div className="flex justify-between items-center mb-4">
        <div className="text-xl font-[700] text-[#232323] flex items-center space-x-2">
          <IoPersonCircleSharp className='w-14 h-14 ml-7' />
          <div>
            <span className='font-medium'>{chat.name}</span>
            <div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
              {chat.status}
            </div>
          </div>
        </div>
      </div>

      {/* Message area */}
      <div className="flex flex-col flex-grow overflow-y-auto bg-[#f6f6f8] p-4">
        {/* Display messages */}
        {messages.map((msg, index) => (
          <div key={index} className={`flex ${msg.sender_mobile === currentUserMobile ? 'justify-end' : ''} mb-4`}>
            {msg.sender_mobile !== currentUserMobile && <img src={chat.avatar || '/assets/default_avatar.png'} alt={msg.sender_name} className="w-10 h-10 rounded-full mr-2" />}
            <div className={`flex flex-col ${msg.sender_mobile === currentUserMobile ? 'items-end' : ''}`}>
              <p className="bg-white p-2 w-[490px] text-xs rounded-lg shadow-md">
                {msg.message}
                <span className="text-xs text-gray-500 float-right" style={{ position: 'relative', top: '-5px', marginRight: '10px' }}>
                  {msg.time}
                </span>
              </p>
            </div>
            {msg.sender_mobile === currentUserMobile && <img src={chat.avatar || '/assets/default_avatar.png'} alt={msg.sender_name} className="w-8 h-8 rounded-full ml-2" />}
          </div>
        ))}
      </div>

      {/* Input area */}
      <div className="flex items-center mb-4 p-4 border-t bg-[#ffffff] shadow-md">
        <input
          type="file"
          id="file-input"
          className="hidden"
          onChange={handleFileChange}
        />
        <label htmlFor="file-input">
          <img className="p-2 mr-2 bg-white w-10 h-10 cursor-pointer" src="/assets/add.svg" alt="Add" />
        </label>
        <input
          type="text"
          value={messageInput}
          onChange={handleInputChange}
          placeholder="Type Here"
          onKeyDown={handleKeyDown}
          className="text-xs font-medium text-gray-400 flex-grow p-2 border rounded-lg outline-none"
        />
        <img
          onClick={handleSendMessage}
          className="p-2 bg-white w-14 h-14 cursor-pointer"
          src="/assets/sendbutton.svg"
          alt="Send"
        />
      </div>
    </div>
  );
};

export default MessageSystem;
