import React, { useState, useEffect } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";
import axios from 'axios';
import { Chat } from './types'; // Import the shared type

interface MessageSystemProps {
  chat: Chat | null;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Chat[]>([]);
  const [file, setFile] = useState<File | null>(null);
  const [user, setUser] = useState<{ mobileNumber: string, name: string, status?: string } | null>(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/user');
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUser();
  }, []);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleSendMessage = async () => {
    if (messageInput.trim() && user && chat) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage: Chat = {
        name: user.name,
        message: messageInput,
        time: formattedTime,
        avatar: chat.avatar || '/assets/default-avatar.svg',
        status: user.status || 'offline',
        mobileNumber: user.mobileNumber,
      };

      try {
        await axios.post('http://localhost:5000/api/messages', {
          sender_mobile: user.mobileNumber,
          receiver_mobile: chat.mobileNumber,
          sender_name: user.name,
          receiver_name: chat.name,
          message: messageInput,
        });
        setMessages([...messages, newMessage]);
        setMessageInput('');
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files ? e.target.files[0] : null;
    if (selectedFile && user && chat) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage: Chat = {
        name: user.name,
        message: `Sent a file: ${selectedFile.name}`,
        time: formattedTime,
        avatar: chat.avatar || '/assets/default-avatar.svg',
        status: user.status || 'offline',
        mobileNumber: user.mobileNumber,
      };

      const formData = new FormData();
      formData.append('file', selectedFile);
      formData.append('sender_mobile', user.mobileNumber);
      formData.append('receiver_mobile', chat.mobileNumber);
      formData.append('sender_name', user.name);
      formData.append('receiver_name', chat.name);
      formData.append('message', `Sent a file: ${selectedFile.name}`);

      try {
        await axios.post('http://localhost:5000/api/messages', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        setMessages([...messages, newMessage]);
        setFile(null);
      } catch (error) {
        console.error('Error sending file:', error);
      }
    }
  };

  if (!chat) {
    return <div>Select a chat to start messaging</div>;
  }

  return (
    <div className="flex flex-col h-full">
      <div className="flex flex-row items-center p-4 bg-gray-100 border-b">
        <div className="flex-shrink-0">
          <IoPersonCircleSharp className='w-12 h-12' />
        </div>
        <div className="ml-2">
          <div className="text-lg font-bold">{chat.name}</div>
          <div className="text-sm">{chat.status}</div>
        </div>
      </div>
      <div className="flex-1 overflow-y-auto p-4">
        {messages.map((message, index) => (
          <div key={index} className={`mb-2 flex ${message.name === user?.name ? 'justify-end' : 'justify-start'}`}>
            <div className={`p-2 rounded ${message.name === user?.name ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>
              <div className="text-xs">{message.name}</div>
              <div className="text-sm">{message.message}</div>
              <div className="text-xs text-gray-500">{message.time}</div>
            </div>
          </div>
        ))}
      </div>
      <div className="p-4 border-t bg-gray-100 flex items-center">
        <input
          type="file"
          onChange={handleFileChange}
          className="mr-2"
        />
        <input
          type="text"
          value={messageInput}
          onChange={handleInputChange}
          placeholder="Type a message..."
          className="flex-grow p-2 border rounded"
        />
        <button onClick={handleSendMessage} className="p-2 ml-2 bg-blue-500 text-white rounded">Send</button>
      </div>
    </div>
  );
};

export default MessageSystem;
