import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MessageSystem from './MessageSystem';
import { IoPersonCircleSharp } from "react-icons/io5";

interface Chat {
  name: string;
  message: string;
  time: string;
  avatar: string;
  status: string;
  mobile: string; // Changed from number to string
}

const Chats: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedChat, setSelectedChat] = useState<Chat | null>(null);
  const [chatData, setChatData] = useState<Chat[]>([]);

  const fetchChatData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/chats');
      setChatData(response.data);
    } catch (error) {
      console.error('Error fetching chat data:', error);
    }
  };

  useEffect(() => {
    fetchChatData();
  }, []);

  const filteredChats = chatData.filter(chat =>
    chat.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="flex flex-row w-full">
      <div className="w-[400px] flex-none bg-[#ffffff] p-4">
        {/* Search bar */}
        <input
          type="text"
          placeholder="Search"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full p-2 border rounded-lg outline-none mb-4"
        />

        {/* List of chats */}
        {filteredChats.map((chat, index) => (
          <div
            key={index}
            className={`flex items-center p-2 border-b cursor-pointer ${selectedChat?.mobile === chat.mobile ? 'bg-gray-100' : ''}`}
            onClick={() => setSelectedChat(chat)}
          >
            <IoPersonCircleSharp className='w-10 h-10' />
            <div className="ml-3">
              <p className="text-lg font-semibold">{chat.name}</p>
              <p className="text-sm text-gray-500">{chat.message}</p>
              <p className="text-xs text-gray-400">{chat.time}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="flex-grow bg-[#f0f0f0]">
        <MessageSystem
          chat={selectedChat}
          sender_mobile="1234567890" // Replace with actual sender mobile
          receiver_mobile={selectedChat?.mobile || ""}
        />
      </div>
    </div>
  );
};

export default Chats;
