import React, { useState, useEffect } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";
import axios from 'axios';

interface Chat {
  name: string;
  message: string;
  time: string;
  avatar: string;
  status: string;
  mobileNumber: string;
}

interface MessageSystemProps {
  chat: Chat | null;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Chat[]>([]);
  const [user, setUser] = useState<{ mobileNumber: string, name: string, status?: string } | null>(null);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [filePreview, setFilePreview] = useState<string | ArrayBuffer | null>(null);

  useEffect(() => {
    // Fetch the logged-in user's details
    const fetchUser = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/user');
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUser();
  }, []);

  useEffect(() => {
    if (selectedFile) {
      const objectUrl = URL.createObjectURL(selectedFile);
      setFilePreview(objectUrl);
      return () => URL.revokeObjectURL(objectUrl); // Cleanup the object URL
    }
  }, [selectedFile]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setSelectedFile(e.target.files[0]);
    }
  };

  const handleSendMessage = async () => {
    if (messageInput.trim() && user && chat) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage: Chat = {
        name: user.name,
        message: messageInput,
        time: formattedTime,
        avatar: chat.avatar || '/assets/default-avatar.svg',
        status: user.status || 'offline',
        mobileNumber: user.mobileNumber,
      };

      setMessages([...messages, newMessage]);
      setMessageInput('');
      setSelectedFile(null); // Clear the file selection after sending the message
    }
  };

  if (!chat) {
    return (
      <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
        <div className="flex justify-between items-center mb-4">
          <div className="text-xl font-[700] mb-5 text-[#232323] flex items-center space-x-2">
            <span>Select a chat to see messages.</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-grow mb-4 flex flex-col h-[calc(100vh-5rem)]">
      <div className="flex justify-between items-center mb-4">
        <div className="text-xl font-[700] text-[#232323] flex items-center space-x-2">
          <IoPersonCircleSharp className='w-14 h-14'/>
          <div>
            <span>{chat.name}</span>
            <div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
              {chat.status}
            </div>
          </div>
        </div>
      </div>

      <div className="flex flex-col flex-grow overflow-y-auto bg-[#f6f6f8] p-4">
        {messages.map((msg, index) => (
          <div key={index} className={`flex ${msg.name === user?.name ? 'justify-end' : ''} mb-4`}>
            {msg.name !== user?.name && <img src={msg.avatar} alt={msg.name} className="w-10 h-10 rounded-full mr-2" />}
            <div className={`flex flex-col ${msg.name === user?.name ? 'items-end' : ''}`}>
              <p className="bg-white p-2 w-[490px] text-xs rounded-lg shadow-md">
                {msg.message}
                <span className="text-xs text-gray-500 float-right" style={{ position: 'relative', top: '-5px', marginRight: '10px' }}>
                  {msg.time}
                </span>
              </p>
            </div>
            {msg.name === user?.name && <img src={msg.avatar} alt={msg.name} className="w-10 h-10 rounded-full ml-2" />}
          </div>
        ))}
        {filePreview && (
          <div className="mt-4">
            <img src={filePreview as string} alt="Preview" className="w-32 h-32 object-cover" />
          </div>
        )}
      </div>

      <div className="flex items-center mb-4 p-4 border-t bg-[#ffffff] shadow-md">
        <input
          type="text"
          value={messageInput}
          onChange={handleInputChange}
          placeholder="Type a message..."
          className="flex-grow p-2 border rounded"
        />
        <input
          type="file"
          onChange={handleFileChange}
          className="ml-2"
        />
        <button onClick={handleSendMessage} className="p-2 ml-2 bg-blue-500 text-white rounded">Send</button>
      </div>
    </div>
  );
};

export default MessageSystem;
