  import React, { useState, useEffect, KeyboardEvent } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";

interface Chat {
  name: string;
  avatar: string;
  status: string;
  mobile: string;
}

interface Message {
  sender_mobile: string;
  receiver_mobile: string;
  sender_name: string;
  receiver_name: string;
  message: string;
  time: string;
}

interface MessageSystemProps {
  chat: Chat | null;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);

  useEffect(() => {
    if (chat) {
      const fetchMessages = async () => {
        try {
          const response = await fetch(`/api/messages?sender_mobile=${chat.mobile}&receiver_mobile=OTHER_MOBILE`);
          const data = await response.json();
          setMessages(data);
        } catch (error) {
          console.error('Error fetching messages:', error);
        }
      };

      fetchMessages();
    }
  }, [chat]);

  const handleSendMessage = async () => {
    if (messageInput.trim() && chat) {
      const now = new Date();
      const formattedTime = now.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: true });

      const newMessage = {
        sender_mobile: chat.mobile,
        receiver_mobile: 'OTHER_MOBILE', // Replace with actual receiver mobile
        sender_name: chat.name,
        receiver_name: chat.name, // Replace with actual receiver name
        message: messageInput,
        time: formattedTime,
      };

      try {
        await fetch('/api/messages', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newMessage),
        });
        setMessages([...messages, newMessage]);
        setMessageInput('');
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSendMessage();
    }
  };

  if (!chat) {
    return (
<div className="flex-grow p-4">
<span>Select a chat to see messages.</span>
</div>
    );
  }

  return (
<div className="flex-grow flex flex-col h-full">
<div className="flex items-center mb-4">
<IoPersonCircleSharp className='w-14 h-14'/>
<div className="ml-2">
<span>{chat.name}</span>
<div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
            {chat.status}
</div>
</div>
</div>
<div className="flex-grow overflow-y-auto">
        {messages.map((msg, index) => (
<div key={index} className={`flex ${msg.sender_mobile === chat.mobile ? 'justify-end' : ''} mb-4`}>
            {msg.sender_mobile !== chat.mobile && <img src={chat.avatar || '/assets/default-avatar.svg'} alt={msg.sender_name} className="w-10 h-10 rounded-full mr-2" />}
<div className={`flex flex-col ${msg.sender_mobile === chat.mobile ? 'items-end' : ''}`}>
<p className="bg-white p-2 rounded-lg shadow-md">
                {msg.message}
<span className="text-xs text-gray-500 float-right">
                  {msg.time}
</span>
</p>
</div>
            {msg.sender_mobile === chat.mobile && <img src={chat.avatar || '/assets/default-avatar.svg'} alt={msg.sender_name} className="w-10 h-10 rounded-full ml-2" />}
</div>
        ))}
</div>
<div className="p-4 flex items-center">
<input
          type="text"
          value={messageInput}
          onChange={(e) => setMessageInput(e.target.value)}
          placeholder="Type Here"
          onKeyDown={handleKeyDown}
          className="flex-grow p-2 border rounded-lg"
        />
<button onClick={handleSendMessage} className="ml-2 p-2 bg-blue-500 text-white rounded-lg">Send</button>
</div>
</div>
  );
};

export default MessageSystem;
