import React, { useState, useEffect } from 'react';

interface Message {
  id: number;
  name: string;
  content: string;
  time: string;
}

const Chats: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [currentTime, setCurrentTime] = useState('');

  // Example messages data
  const messages: Message[] = [
    { id: 1, name: 'Vijay', content: 'James Added former jasmine', time: 'Just now' },
    { id: 2, name: 'Jasmine', content: 'James Added former jasmine', time: 'Just now' },
    { id: 3, name: 'James', content: 'James Added former jasmine', time: 'Just now' },
    { id: 4, name: 'Karthi', content: 'James Added former jasmine', time: 'Just now' },
    { id: 5, name: 'Shiek', content: 'James Added former jasmine', time: 'Just now' }
  ];

  useEffect(() => {
    const now = new Date();
    const options: Intl.DateTimeFormatOptions = {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    };
    const formattedTime = now.toLocaleTimeString([], options);
    setCurrentTime(formattedTime);
  }, []);

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="flex flex-col bg-white p-5 rounded-lg mx-4 ml-56 h-auto w-[28%] shadow-2xl" style={{ borderRightWidth: "1px" }}>
      {/* Search Bar */}
      <div className="flex justify-center mb-4 mt-3">
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search here"
          className="w-[75%] p-2 pl-10 pr-10 rounded-lg bg-gray-100 placeholder:text-xs text-left focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      <div className="left-3 ml-2 z-3 flex flex-col space-y-0">
        <div className="flex gap-2.5 font-work-sans">
          <div className="text-green-500">
            <button className="relative bg-none p-2.5 border-b-2 border-green-500 cursor-pointer text-green-500 text-xs">
              Recent Chats
            </button>
          </div>
          <button className="border-none bg-none p-2.5 cursor-pointer text-gray-400 text-xs">All Chats</button>
        </div>
        <div className="line text-gray-200">
          <hr className="m-0 w-[96%]" />
        </div>
      </div>

      {messages.map((message) => (
        <div key={message.id} className="bg-white p-4 h-16 mt-1 rounded mb-1">
          <div className='flex flex-row'>
            <img
              className="w-8 h-8 ml-2"
              src='/assets/vijay.svg'
              alt="Profile"
            />
            <div className='flex flex-col ml-1'>
              <div className='text-xs font-semibold'>{message.name}</div>
              <div className='text-xs whitespace-nowrap'>{message.content}</div>
            </div>
            <div className='text-xs ml-6'>{message.time}</div>
          </div>
        </div>
      ))}

      <div className="line text-gray-200">
        <hr className="m-0 w-[96%]" />
      </div>

    </div>
  );
};

export default Chats;
