import React, { useState } from 'react';
import { BsCheckCircle } from 'react-icons/bs';
import { RiProhibitedLine } from 'react-icons/ri';

const Buttons: React.FC = () => {
  const [isPopupVisible, setIsPopupVisible] = useState(false);

  const handleRejectClick = () => {
    setIsPopupVisible(true);
  };

  const handleCancelClick = () => {
    setIsPopupVisible(false);
  };

  const handleConfirmClick = () => {
    // Handle confirm logic here
    setIsPopupVisible(false);
  };

  return (
<div className="flex ml-8 mb-20 mt-10 z-20 space-x-2">
<button className="bg-green-600 text-white text-xs px-4 py-2 rounded flex items-center">
<BsCheckCircle className="mr-2" /> Approve
</button>
<button
        className="text-white text-xs px-6 py-2 rounded flex items-center"
        style={{ backgroundColor: "#F75656" }}
        onClick={handleRejectClick}
>
<RiProhibitedLine className="mr-2" /> Reject
</button>

      {isPopupVisible && (
<div className="fixed inset-0 flex items-center justify-center bg-[#f2f4f2] w- bg-opacity-50 z-50">
<div className="bg-white p-6 rounded shadow-lg">
<h2 className="text-xl mb-5 bg-red-500">Rejected</h2>
<div className='mb-36'>
  <h2 className='text-gray-300 bg-[#f2f4f2]' >Enter the rejected the reason</h2>
  <div className='flex justify-center mt-80'>
  <h3>20/</h3>
  <h4 className='text-gray-400'>180</h4>
  </div>
</div>
<div className="flex space-x-4">
<button
                className="bg-gray-300 text-gray-700 px-4 py-2 rounded"
                onClick={handleCancelClick}
>
                Cancel
</button>
<button
                className="bg-red-600 text-white px-4 py-2 rounded"
                onClick={handleConfirmClick}
>
                Confirm
</button>
</div>
</div>
</div>
      )}
</div>
  );
};

export default Buttons;
