import React, { useState, useEffect } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import axios from 'axios';
import MessageSystem from './MessageSystem';
import { IoPersonCircleSharp } from "react-icons/io5";

interface Chat {
  name: string;
  message: string;
  time: string;
  avatar: string;
  status: string;
  mobile: string; // Use string for 


  mobileNumber: string;
}

const Chats: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [currentTime, setCurrentTime] = useState('');
  const [selectedChat, setSelectedChat] = useState<Chat | null>(null);
  const [chatData, setChatData] = useState<Chat[]>([]);

  // Fetch chat data from the API
  const fetchChatData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/chats');
      setChatData(response.data);
    } catch (error) {
      console.error('Error fetching chat data:', error);
    }
  };

  useEffect(() => {
    fetchChatData();
  }, []);

  useEffect(() => {
    if (selectedChat) {
      axios.get('http://localhost:5000/api/messages', {
        params: {
          sender_mobile: selectedChat.mobile,
          receiver_mobile: 'OTHER_MOBILE' // Replace with actual receiver's mobile
        }
      }).then(response => {
        setChatData(response.data);
      }).catch(error => {
        console.error('Error fetching messages:', error);
      });
    }
  }, [selectedChat]);

  // Update current time
  useEffect(() => {
    const now = new Date();
    const options: Intl.DateTimeFormatOptions = {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
    };
    const formattedTime = now.toLocaleTimeString([], options);
    setCurrentTime(formattedTime);

  }, []);

  // Refresh chat data every 30 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      fetchChatData();
    }, 30000);

    return () => clearInterval(interval);
  }, []);

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  const handleChatClick = (chat: Chat) => {
    setSelectedChat(chat);
  };

  const filteredChats = chatData.filter(chat =>
    chat.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const currentUserMobile='995263807';

  return (
    <div className='flex h-screen'>
      <div className="flex flex-col mx-4 ml-5 w-1/3 h-530 shadow-md">
        {/* Search Bar */}
        <div className="flex justify-center mb-4 mt-3">
          <input
            type="text"
            value={searchTerm}
            onChange={handleSearchChange}
            placeholder="Search here"
            className="w-[85%] p-2 rounded-lg placeholder:text-xs text-left outline-none"
          />
          <i className="fa fa-search absolute right-10 top-3 text-gray-400"></i>
        </div>

        <div className="ml-6 z-2 flex flex-col mr-[30%] space-y-0">
          <div className="flex gap-2.5 font-work-sans">
            <div className="text-green-500">
              <button className="bg-none p-2.5 border-b-2 border-green-500 cursor-pointer text-green-500 text-xs">
                Recent Chats
              </button>
            </div>
            <button className="border-none bg-none p-2.5 cursor-pointer text-gray-400 text-xs">All Chats</button>
          </div>
          <div className="line text-gray-200">
            <hr className="m-0 w-[100%]" />
          </div>
        </div>

        {/* Scrollable Chat List */}
        <div className="flex-1 overflow-y-auto">
          {filteredChats.map((chat, index) => (
            <div
              key={index}
              onClick={() => handleChatClick(chat)}
              className={`cursor-pointer ${selectedChat?.mobile === chat.mobile ? 'bg-gray-100' : ''}`}
            >
              <div className=
"bg-white p-4 h-16 mt-1 rounded mb-1">
                <div className='flex flex-row items-center'>
                  <div className="relative">
                    <IoPersonCircleSharp className='w-9 h-9' />
                  </div>
                  <div className='flex flex-col ml-1'>
                    <div className='text-xs font-semibold' style={{ color: "#232323" }}>
                      {chat.name}
                    </div>
                    <div className='text-xs whitespace-nowrap'>{chat.message}</div>
                  </div>
                  <div className='text-xs ml-6'>{chat.time}</div>
                </div>
              </div>
              <div className="line ml-6 text-gray-200">
                <hr className="m-0 w-[93%]" />
              </div>
            </div>
          ))}
        </div>
      </div>
      <div className="flex-grow h-full bg-[#f0f0f0]">
        <MessageSystem chat={selectedChat} />

      </div>
    </div>
  );
};

export default Chats;
