// MessageSystem.tsx
import React, { useState, useEffect } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";
import axios from 'axios';

interface Chat {
  id: number;
  name: string;
  avatar: string;
  status: string;
}

interface Message {
  id: number;
  chat_id: number;
  sender_name: string;
  message: string;
  time: string;
  status: string;
  file_url?: string;
}

interface MessageSystemProps {
  chat: Chat | null;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [file, setFile] = useState<File | null>(null);

  useEffect(() => {
    if (chat) {
      axios.get(`/api/messages/chat/${chat.id}`)
        .then(response => setMessages(response.data))
        .catch(error => console.error('Error fetching messages:', error));
    }
  }, [chat]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleSendMessage = () => {
    if (messageInput.trim() || file) {
      const formData = new FormData();
      formData.append('chatId', chat?.id.toString() || '');
      formData.append('senderName', chat?.name || 'User');
      formData.append('message', messageInput);
      formData.append('status', chat?.status || 'offline');
      if (file) {
        formData.append('file', file);
      }

      axios.post('/api/messages/send', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      })
      .then(() => {
        setMessageInput('');
        setFile(null);
        return axios.get(`/api/messages/chat/${chat?.id}`);
      })
      .then(response => setMessages(response.data))
      .catch(error => console.error('Error sending message:', error));
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFile(e.target.files ? e.target.files[0] : null);
  };

  if (!chat) {
    return (
      <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
        <div className="flex justify-between items-center mb-4">
          <div className="text-xl font-[600] mb-5 text-[#232323] flex items-center space-x-2">
            <span>Select a chat to see messages.</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-grow mb-4 flex flex-col h-[calc(100vh-5rem)]">
      {/* Header with chat information */}
      <div className="flex justify-between items-center mb-4">
        <div className="text-xl font-[700] text-[#232323] flex items-center space-x-2">
          <div><IoPersonCircleSharp className='w-14 h-14' /></div>
          <div>
            <span className='font-medium'>{chat.name}</span>
            <div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
              {chat.status}
            </div>
          </div>
        </div>
      </div>

      {/* Message area */}
      <div className="flex flex-col flex-grow overflow-y-auto bg-[#f6f6f8] p-4">
        {/* Display messages */}
        {messages.map((msg) => (
          <div key={msg.id} className={`flex ${msg.sender_name === chat?.name ? 'justify-end' : ''} mb-4`}>
            {msg.sender_name !== chat?.name && <img src={chat.avatar} alt={chat.name} className="w-10 h-10 rounded-full mr-2" />}
            <div className={`flex flex-col ${msg.sender_name === chat?.name ? 'items-end' : ''}`}>
              <p className="bg-white p-2 w-[490px] text-xs rounded-lg shadow-md">
                {msg.message}
                {msg.file_url && <a href={msg.file_url} download className="text-xs text-blue-500">Download File</a>}
                <span className="text-xs text-gray-500 float-right" style={{ position: 'relative', top: '-5px', marginRight: '10px' }}>
                  {msg.time}
                </span>
              </p>
            </div>
            {msg.sender_name === chat?.name && <img src={chat.avatar} alt={chat.name} className="w-10 h-10 rounded-full ml-2" />}
          </div>
        ))}
      </div>

      {/* Input area */}
      <div className="flex items-center mb-4 p-4 border-t bg-[#ffffff] shadow-md">
        <input
          type="file"
          id="file-input"
          className="hidden"
          onChange={handleFileChange}
        />
        <label htmlFor="file-input">
          <img className="p-2 mr-2 bg-white w-10 h-10 cursor-pointer" src="/assets/add.svg" alt="Plus" />
        </label>
        <input
          type="text"
          value={messageInput}
          onChange={handleInputChange}
          placeholder="Type Here"
          className="text-xs font-medium text-gray-400 flex-grow p-2 border rounded-lg outline-none"
        />
        <button
          onClick={handleSendMessage}
          className="p-2 bg-blue-500 text-white rounded"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default MessageSystem;
