import React, { useState, useEffect } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";
import axios from 'axios';
import { Chat } from '../types';

interface MessageSystemProps {
  chat: Chat | null;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Chat[]>([]);
  const [file, setFile] = useState<File | null>(null);
  const [user, setUser] = useState<{ mobileNumber: string, name: string, status?: string } | null>(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/user');
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUser();
  }, []);

  useEffect(() => {
    if (chat) {
      const fetchMessages = async () => {
        try {
          const response = await axios.get('http://localhost:5000/api/messages', {
            params: {
              sender_mobile: user?.mobileNumber || '',
              receiver_mobile: chat.mobileNumber
            }
          });
          setMessages(response.data);
        } catch (error) {
          console.error('Error fetching messages:', error);
        }
      };

      fetchMessages();
    }
  }, [chat, user]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleSendMessage = async () => {
    if (messageInput.trim() && user && chat) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage: Chat = {
        name: user.name,
        message: messageInput,
        time: formattedTime,
        avatar: chat.avatar || '/assets/default-avatar.svg',
        status: user.status || 'offline',
        mobileNumber: user.mobileNumber,
        receiver_name: '',
        receiver_mobile: ''
      };

      try {
        await axios.post('http://localhost:5000/api/messages', {
          sender_mobile: user.mobileNumber,
          receiver_mobile: chat.mobileNumber,
          sender_name: user.name,
          receiver_name: chat.name,
          message: messageInput,
        });
        setMessages([...messages, newMessage]);
        setMessageInput('');
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files ? e.target.files[0] : null;
    if (selectedFile && user && chat) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage: Chat = {
        name: user.name,
        message: `Sent a file: ${selectedFile.name}`,
        time: formattedTime,
        avatar: chat.avatar || '/assets/default-avatar.svg',
        status: user.status || 'offline',
        mobileNumber: user.mobileNumber,
        receiver_name: '',
        receiver_mobile: ''
      };

      const formData = new FormData();
      formData.append('file', selectedFile);
      formData.append('sender_mobile', user.mobileNumber);
      formData.append('receiver_mobile', chat.mobileNumber);
      formData.append('sender_name', user.name);
      formData.append('receiver_name', chat.name);
      formData.append('message', `Sent a file: ${selectedFile.name}`);

      try {
        await axios.post('http://localhost:5000/api/messages', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        setMessages([...messages, newMessage]);
        setFile(null);
      } catch (error) {
        console.error('Error sending file:', error);
      }
    }
  };

  if (!chat) {
    return (
      <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
        <div className="flex justify-between items-center mb-4">
          <div className="text-xl font-[700] mb-5 text-[#232323] flex items-center space-x-2">
            <span>Select a chat to see messages.</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
      <div className="flex items-center space-x-2 mb-4">
        <IoPersonCircleSharp className="text-3xl" />
        <div className="text-xl font-[700] mb-5 text-[#232323]">{chat.name}</div>
      </div>

      <div className="flex flex-col flex-grow overflow-y-auto p-4 bg-gray-100 rounded-lg">
        {messages.map((msg, index) => (
          <div key={index} className={`flex items-start ${msg.name === user?.name ? 'justify-end' : 'justify-start'}`}>
            <div className={`bg-white p-3 rounded-lg shadow-md ${msg.name === user?.name ? 'bg-green-200' : 'bg-gray-200'}`}>
              <div className="text-xs font-medium">{msg.name}</div>
              <div className="text-sm">{msg.message}</div>
              <div className="text-xs text-gray-500">{msg.time}</div>
            </div>
          </div>
        ))}
      </div>

      <div className="flex mt-4">
        <input
          type="text"
          value={messageInput}
          onChange={handleInputChange}
          placeholder="Type a message"
          className="flex-grow p-2 border border-gray-300 rounded-l-lg"
        />
        <input
          type="file"
          onChange={handleFileChange}
          className="ml-2 border-gray-300 rounded-r-lg"
        />
        <button
          onClick={handleSendMessage}
          className="bg-blue-500 text-white p-2 rounded-r-lg"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default MessageSystem;
