import React, { useState, useEffect, KeyboardEvent } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";

interface Chat {
  name: string;
  avatar: string;
  status: string;
  mobile: string; // Ensure this matches the prop you're passing
}

interface Message {
  sender_mobile: string;
  receiver_mobile: string;
  sender_name: string;
  receiver_name: string;
  message: string;
  time: string;
}

interface MessageSystemProps {
  chat: Chat | null;
  currentUserMobile: string; // Add this prop to pass current user's mobile number
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat, currentUserMobile }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [file, setFile] = useState<File | null>(null);

  useEffect(() => {
    if (chat) {
      const fetchMessages = async () => {
        try {
          const response = await fetch(`/api/messages?sender_mobile=${currentUserMobile}&receiver_mobile=${chat.mobile}`);
          const data = await response.json();
          setMessages(data);
        } catch (error) {
          console.error('Error fetching messages:', error);
        }
      };

      fetchMessages();
    }
  }, [chat, currentUserMobile]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleSendMessage = async () => {
    if (messageInput.trim()) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage = {
        sender_mobile: currentUserMobile,
        receiver_mobile: chat?.mobile,
        sender_name: 'User', // Replace with actual sender name
        receiver_name: chat?.name || 'User',
        message: messageInput,
        time: formattedTime,
      };

      try {
        await fetch('/api/messages', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newMessage),
        });
        setMessages([...messages, newMessage]);
        setMessageInput(''); // Clear input after sending
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files ? e.target.files[0] : null;
    if (selectedFile) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage = {
        sender_mobile: currentUserMobile,
        receiver_mobile: chat?.mobile,
        sender_name: 'User',
        receiver_name: chat?.name || 'User',
        message: `Sent a file: ${selectedFile.name}`,
        time: formattedTime,
      };

      try {
        await fetch('/api/messages', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newMessage),
        });
        setMessages([...messages, newMessage]);
        setFile(selectedFile);
      } catch (error) {
        console.error('Error sending file:', error);
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <div className='flex flex-col h-full'>
      <div className="flex flex-col flex-grow p-4 bg-white">
        {messages.map((msg, index) => (
          <div key={index} className={`flex ${msg.sender_mobile === currentUserMobile ? 'justify-end' : 'justify-start'} mb-2`}>
            <div className={`p-2 rounded ${msg.sender_mobile === currentUserMobile ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>
              <div className="text-xs">{msg.message}</div>
              <div className="text-xs text-gray-500">{msg.time}</div>
            </div>
          </div>
        ))}
      </div>
      <div className="flex items-center p-4 bg-gray-100">
        <input
          type="text"
          value={messageInput}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          placeholder="Type your message"
          className="flex-grow p-2 rounded border"
        />
        <button onClick={handleSendMessage} className="ml-2 px-4 py-2 bg-blue-500 text-white rounded">
          Send
        </button>
        <input type="file" onChange={handleFileChange} className="ml-2" />
      </div>
    </div>
  );
};

export default MessageSystem;
