import React, { useState } from 'react';

const Chats: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="flex flex-col bg-gray-100 p-5 rounded-lg mx-4 mt-2 ml-56 mr-50 h-80 w-[25%] shadow-lg" style={{ borderRightWidth: "1px" }}>
      {/* Search Bar */}
      <div className="mb-4 mt-3 ml-8">
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search..."
          className="w-[90%] p-1 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      {/* Chat Content */}
      <div className="flex-1 overflow-y-auto">
        {/* Add your chat content here */}
      </div>
    </div>
  );
};

export default Chats;
