import React, { useState, useEffect } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";

// Define the structure of a chat message
interface Chat {
  name: string;
  message: string;
  time: string;
  avatar: string;
  status: string; // Added status field
}

// Define props for the MessageSystem component
interface MessageSystemProps {
  chat: Chat | null;
}

// Define the MessageSystem component
const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Chat[]>([]); // Array to store messages
  const [file, setFile] = useState<File | null>(null); // State to handle file
  const [socket, setSocket] = useState<WebSocket | null>(null);

  // Initialize WebSocket connection when the component mounts
  useEffect(() => {
    const ws = new WebSocket('ws://localhost:8080'); // Replace with your WebSocket server URL
    setSocket(ws);

    // Handle incoming messages
    ws.onmessage = (event) => {
      const incomingMessage = JSON.parse(event.data);
      setMessages((prevMessages) => [...prevMessages, incomingMessage]);
    };

    // Clean up WebSocket connection when the component unmounts
    return () => {
      ws.close();
    };
  }, []);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleSendMessage = () => {
    if (messageInput.trim()) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage = {
        name: chat?.name || 'User',
        message: messageInput,
        time: formattedTime,
        avatar: chat?.avatar || '/assets/default-avatar.svg',
        status: chat?.status || 'offline',
      };

      // Send message via WebSocket
      socket?.send(JSON.stringify(newMessage));

      setMessages([...messages, newMessage]);
      setMessageInput(''); // Clear input after sending
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files ? e.target.files[0] : null;
    if (selectedFile) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage = {
        name: chat?.name || 'User',
        message: `Sent a file: ${selectedFile.name}`,
        time: formattedTime,
        avatar: chat?.avatar || '/assets/default-avatar.svg',
        status: chat?.status || 'offline',
      };

      // Send file message via WebSocket
      socket?.send(JSON.stringify(newMessage));

      setMessages([...messages, newMessage]);
      setFile(null); // Clear the file after sending
    }
  };

  if (!chat) {
    return (
      <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
        <div className="flex justify-between items-center mb-4">
          <div className="text-xl font-[700] mb-5 text-[#232323] flex items-center space-x-2">
            <span>Select a chat to see messages.</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-grow mb-4 flex flex-col h-[calc(100vh-5rem)]">
      {/* Header with chat information */}
      <div className="flex justify-between items-center mb-4">
        <div className="text-xl font-[700] text-[#232323] flex items-center space-x-2">
          <div><IoPersonCircleSharp className='w-14 h-14'/></div>
          <div>
            <span>{chat.name}</span>
            <div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
              {chat.status}
            </div>
          </div>
        </div>
      </div>

      {/* Message area */}
      <div className="flex flex-col flex-grow overflow-y-auto bg-[#f6f6f8] p-4">
        {/* Display messages */}
        {messages.map((msg, index) => (
          <div key={index} className={`flex ${msg.name === chat?.name ? 'justify-end' : ''} mb-4`}>
            {msg.name !== chat?.name && <img src={msg.avatar} alt={msg.name} className="w-10 h-10 rounded-full mr-2" />}
            <div className={`flex flex-col ${msg.name === chat?.name ? 'items-end' : ''}`}>
              <p className="bg-white p-2 w-[490px] text-xs rounded-lg shadow-md">
                {msg.message}
                <span className="text-xs text-gray-500 float-right" style={{ position: 'relative', top: '-5px', marginRight: '10px' }}>
                  {msg.time}
                </span>
              </p>
            </div>
            {msg.name === chat?.name && <img src={msg.avatar} alt={msg.name} className="w-10 h-10 rounded-full ml-2" />}
          </div>
        ))}
      </div>

      {/* Input area */}
      <div className="flex items-center mb-4 p-4 border-t bg-[#ffffff] shadow-md">
        <input
          type="file"
          id="file-input"
          className="hidden"
          onChange={handleFileChange}
        />
        <label htmlFor="file-input">
          <img className="p-2 mr-2 bg-white w-10 h-10 cursor-pointer" src="/assets/add.svg" alt="Plus" />
        </label>
        <input
          type="text"
          value={messageInput}
          onChange={handleInputChange}
          placeholder="Type Here"
          className="text-xs font-medium text-gray-400 flex-grow p-2 border rounded-lg outline-none" 
        />
        <img
          onClick={handleSendMessage}
          className="p-2 bg-white w-14 h-14 cursor-pointer"
          src="/assets/sendbutton.svg"
          alt="Send"
        />
      </div>
    </div>
  );
};

export default MessageSystem;
