import React, { useState, useEffect } from 'react';

interface Chat {
  mobile: string;
  name: string;
  status: string;
  avatar?: string;
}

interface ChatsProps {
  onSelectChat: (chat: Chat) => void;
}

const Chats: React.FC<ChatsProps> = ({ onSelectChat }) => {
  const [chats, setChats] = useState<Chat[]>([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchChats = async () => {
      try {
        const response = await fetch('/api/chats');
        const data = await response.json();
        setChats(data);
      } catch (error) {
        console.error('Error fetching chats:', error);
      }
    };

    fetchChats();
  }, []);

  const filteredChats = chats.filter((chat) =>
    chat.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-4 bg-white border-r h-[calc(100vh-5rem)] overflow-y-auto">
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Search chats"
        className="w-full p-2 border rounded-lg mb-4"
      />
      {filteredChats.map((chat) => (
        <div
          key={chat.mobile}
          className="flex items-center p-2 mb-2 cursor-pointer hover:bg-gray-100 rounded-lg"
          onClick={() => onSelectChat(chat)}
        >
          <img
            src={chat.avatar || '/assets/default-avatar.svg'}
            alt={chat.name}
            className="w-10 h-10 rounded-full mr-2"
          />
          <div>
            <div className="font-medium">{chat.name}</div>
            <div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
              {chat.status}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Chats;
