import React, { useState, useEffect } from 'react';



interface Item {

  url: string;

  name: string;

  image: string;

  status: string;

}



const items: Item[] = [

  { url: '', name: 'Sathish', image: '/assets/sathish.svg', status: 'online' },

];



interface Message {

  name: string;

  content: string;

  time: string;

  sender: boolean; // true if the message is sent by the current user, false if received

  type: string;

}



const MessageSystem: React.FC = () => {

  const [messages, setMessages] = useState<Message[]>([]);

  const [inputValue, setInputValue] = useState('');

  const [fileInput, setFileInput] = useState<File | null>(null);

  const [ws, setWs] = useState<WebSocket | null>(null);

  const [typingStatus, setTypingStatus] = useState<string | null>(null);



  useEffect(() => {

    const websocket = new WebSocket('ws://localhost:5001');

    setWs(websocket);



    websocket.onmessage = (event) => {

      if (event.data instanceof Blob) {

        const reader = new FileReader();

        reader.onload = () => {

          try {

            const data = JSON.parse(reader.result as string);

            handleIncomingMessage(data);

          } catch (error) {

            console.error('Error parsing WebSocket message:', error);

          }

        };

        reader.readAsText(event.data);

      } else {

        try {

          const data = JSON.parse(event.data);

          handleIncomingMessage(data);

        } catch (error) {

          console.error('Error parsing WebSocket message:', error);

        }

      }

    };



    websocket.onerror = (error) => {

      console.error('WebSocket error:', error);

    };



    return () => {

      websocket.close();

    };

  }, []);



  const handleIncomingMessage = (data: any) => {

    if (Array.isArray(data)) {

      setMessages(data.map((msg: any) => ({

        name: msg.name,

        content: msg.content,

        time: msg.time,

        sender: msg.sender,

        type: msg.type

      })));

    } else {

      setMessages(prevMessages => [...prevMessages, data.message]);

    }

  };



  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {

    setInputValue(e.target.value);

    if (ws) {

      ws.send(JSON.stringify({ type: 'typing', typingStatus: 'Typing...', user: 'You' }));

    }

  };



  const handleSendMessage = async () => {

    if (inputValue.trim()) {

      const newMessage = {

        name: 'You',

        content: inputValue,

        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),

        sender: true,

        type: 'text'

      };



      await fetch('http://localhost:5001/api/messages', {

        method: 'POST',

        headers: { 'Content-Type': 'application/json' },

        body: JSON.stringify(newMessage)

      });



      setInputValue('');

      if (ws) {

        ws.send(JSON.stringify({ type: 'message', message: newMessage }));

      }

    }

  };



  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {

    if (e.target.files && e.target.files[0]) {

      const file = e.target.files[0];

      setFileInput(file);



      const formData = new FormData();

      formData.append('file', file);



      const response = await fetch('http://localhost:5001/api/upload', {

        method: 'POST',

        body: formData

      });



      if (response.ok) {

        const result = await response.json();

        const fileUrl = result.fileUrl;



        const newMessage = {

          name: 'You',

          content: fileUrl,

          time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),

          sender: true,

          type: file.type.startsWith('image/') ? 'image' : 'file'

        };



        setMessages([...messages, newMessage]);

        if (ws) {

          ws.send(JSON.stringify({ type: 'message', message: newMessage }));

        }

      } else {

        console.error('File upload failed');

      }

    }

  };



  return (

    <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">

      <div className="flex justify-between items-center mb-4">

        <div className="text-xl font-[700] mb-5 text-[#232323] flex items-center space-x-2">

          <span>Message System</span>

        </div>

      </div>

      <div className="flex flex-col flex-grow overflow-hidden">

        {items.map((item, index) => (

          <div key={index} className="flex flex-col flex-grow overflow-hidden">

            <div className="flex items-center ml-5 p-4 border h-[80px] w-[90%] bg-[#ffffff] shadow-md">

              <img

                className="p-2 mr-7 ml-5 bg-[#fbfbfb] w-8 h-8 rounded-md"

                src="/assets/arrow.svg"

                alt="Arrow"

              />

              <img

                className="w-12 h-12 mr-4"

                src={item.image}

                alt={item.name}

              />

              <div>

                <p className="text-base font-medium">{item.name}</p>

                <span className={`text-sm ${item.status === 'online' ? 'text-green-500' : 'text-gray-500'}`}>

                  {item.status === 'online' ? 'Online' : 'Offline'}

                </span>

              </div>

            </div>

            <div className="flex-1 ml-5 p-4 bg-[#f6f6f8] w-[90%] overflow-y-auto flex-grow">

              {messages.map((message, idx) => (

                <div key={idx} className={`flex ${message.sender ? 'justify-end' : 'justify-start'} mb-4`}>

                  {!message.sender && (

                    <img

                      src="/assets/sathish.svg"

                      alt="User"

                      className="w-8 h-8 rounded-full mr-2"

                    />

                  )}

                  <div className={`p-2 rounded-lg ${message.sender ? 'bg-blue-500 text-white' : 'bg-white text-black'} ${message.sender ? 'ml-2' : 'mr-2'} max-w-[60%]`}>

                    {!message.sender && (

                      <p className="font-bold mb-1">{message.name}</p>

                    )}

                    {message.type === 'image' ? (

                      <img src={message.content} alt="Sent file" className="w-full h-auto rounded-lg" />

                    ) : (

                      <p>{message.content}</p>

                    )}

                    <p className="text-xs text-gray-500">{message.time}</p>

                  </div>

                </div>

              ))}

            </div>

          </div>

        ))}

      </div>

      <div className="flex items-center ml-5 mb-4 p-4 border h-[65px] w-[90%] bg-[#ffffff] shadow-md">

        <label htmlFor="file-upload" className="p-2 mr-2 bg-white w-10 h-10 cursor-pointer">

          <img

            className="w-full h-full"

            src="/assets/plus.svg"

            alt="Plus"

          />

          <input

            id="file-upload"

            type="file"

            className="hidden"

            onChange={handleFileChange}

          />

        </label>

        <input

          type="text"

          value={inputValue}

          onChange={handleInputChange}

          placeholder="Type Here"

          className="text-sm font-medium text-gray-400 flex-grow outline-none"

        />

        <img

          className="p-2 bg-white w-14 h-14 cursor-pointer"

          src="/assets/ws.svg"

          alt="Send"

          onClick={handleSendMessage}

        />

      </div>

    </div>

  );

};



export default MessageSystem;
