import React, { useState, useEffect } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";
import axios from 'axios';

interface Item {
  url: string;
  name: string;
  image: string;
  status: string;
}

const items: Item[] = [
  { url: '', name: 'Sathish', image: '/assets/sathish.svg', status: 'online' },
];

interface Message {
  name: string;
  content: string;
  time: string;
  sender: boolean; // true if the message is sent by the current user, false if received
  type: string;
}

interface Chat {
  name: string;
  mobileNumber: string;
  avatar: string;
  status: string;
}

interface MessageSystemProps {
  chat: Chat | null;
  loggedInUserMobileNumber: string;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat, loggedInUserMobileNumber }) => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [fileInput, setFileInput] = useState<File | null>(null);
  const [ws, setWs] = useState<WebSocket | null>(null);
  const [typingStatus, setTypingStatus] = useState<string | null>(null);
  const [user, setUser] = useState<{ name: string; status: string } | null>(null);

  useEffect(() => {
    // WebSocket connection
    const websocket = new WebSocket('ws://localhost:5001');
    setWs(websocket);

    websocket.onmessage = (event) => {
      if (event.data instanceof Blob) {
        const reader = new FileReader();
        reader.onload = () => {
          try {
            const data = JSON.parse(reader.result as string);
            handleIncomingMessage(data);
          } catch (error) {
            console.error('Error parsing WebSocket message:', error);
          }
        };
        reader.readAsText(event.data);
      } else {
        try {
          const data = JSON.parse(event.data);
          handleIncomingMessage(data);
        } catch (error) {
          console.error('Error parsing WebSocket message:', error);
        }
      }
    };

    websocket.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    return () => {
      websocket.close();
    };
  }, []);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/user/${loggedInUserMobileNumber}`);
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUser();
  }, [loggedInUserMobileNumber]);

  const handleIncomingMessage = (data: any) => {
    if (Array.isArray(data)) {
      setMessages(data.map((msg: any) => ({
        name: msg.name,
        content: msg.content,
        time: msg.time,
        sender: msg.sender,
        type: msg.type
      })));
    } else {
      setMessages(prevMessages => [...prevMessages, data.message]);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(e.target.value);
    if (ws) {
      ws.send(JSON.stringify({ type: 'typing', typingStatus: 'Typing...', user: 'You' }));
    }
  };

  const handleSendMessage = async () => {
    if (inputValue.trim() && user && chat) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage = {
        name: user.name,
        content: inputValue,
        time: formattedTime,
        sender: true,
        type: 'text'
      };

      try {
        await axios.post('http://localhost:5000/api/messages', {
          sender_mobile: loggedInUserMobileNumber,
          receiver_mobile: chat.mobileNumber,
          sender_name: user.name,
          receiver_name: chat.name,
          message: inputValue,
        });

        setMessages([...messages, newMessage]);
        setInputValue('');

        if (ws) {
          ws.send(JSON.stringify({ type: 'message', message: newMessage }));
        }
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0] && user && chat) {
      const file = e.target.files[0];
      setFileInput(file);

      const formData = new FormData();
      formData.append('file', file);
      formData.append('sender_mobile', loggedInUserMobileNumber);
      formData.append('receiver_mobile', chat.mobileNumber);
      formData.append('sender_name', user.name);
      formData.append('receiver_name', chat.name);

      try {
        const response = await axios.post('http://localhost:5000/api/messages', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });

        if (response.status === 200) {
          const fileUrl = response.data.fileUrl;

          const newMessage = {
            name: user.name,
            content: fileUrl,
            time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
            sender: true,
            type: file.type.startsWith('image/') ? 'image' : 'file'
          };

          setMessages([...messages, newMessage]);

          if (ws) {
            ws.send(JSON.stringify({ type: 'message', message: newMessage }));
          }
        } else {
          console.error('File upload failed');
        }
      } catch (error) {
        console.error('Error sending file:', error);
      }
    }
  };

  if (!chat) {
    return (
      <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
        <div className="flex justify-between items-center mb-4">
          <div className="text-xl font-[600] mb-5 text-[#232323] flex items-center space-x-2">
            <span>Select a chat to see messages.</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-grow mb-4 flex flex-col h-[calc(100vh-5rem)]">
      {/* Header with chat information */}
      <div className="flex justify-between items-center mb-4">
        <div className="text-xl font-[700] text-[#232323] flex items-center space-x-2">
          <IoPersonCircleSharp className='w-14 h-14' />
          <div>
            <span className='font-medium'>{chat.name}</span>
            <div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
              {chat.status}
            </div>
          </div>
        </div>
      </div>

      {/* Message area */}
      <div className="flex flex-col flex-grow overflow-y-auto bg-[#f6f6f8] p-4">
        {/* Display messages */}
        {messages.map((msg, index) => (
          <div key={index} className={`flex ${msg.sender ? 'justify-end' : 'justify-start'} mb-4`}>
            {!msg.sender && (
              <img
                src="/assets/sathish.svg"
                alt="User"
                className="w-8 h-8 rounded-full mr-2"
              />
            )}
            <div className={`p-2 rounded-lg ${msg.sender ? 'bg-blue-500 text-white' : 'bg-white text-black'} ${msg.sender ? 'ml-2' : 'mr-2'} max-w-[60%]`}>
              {!msg.sender && (
                <p className="font-bold mb-1">{msg.name}</p>
              )}
              {msg.type === 'image' ? (
                <img src={msg.content} alt="Sent file" className="w-full h-auto rounded-lg" />
              ) : (
                <p>{msg.content}</p>
              )}
              <p className="text-xs text-gray-500">{msg.time}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Input area */}
      <div className="flex items-center mb-4 p-4 border-t bg-[#ffffff] shadow-md">
        <input
          type="file"
          id="file-input"
          className="hidden"
          onChange={handleFileChange}
        />
        <label htmlFor="file-input">
          <img className="p-2 mr-2 bg-white w-10 h-10 cursor-pointer" src="/assets/add.svg" alt="Add" />
        </label>
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Type a message..."
          className="flex-grow border-2 border-gray-300 rounded-md p-2"
        />
        <button
          onClick={handleSendMessage}
          className="ml-2 px-4 py-2 bg-blue-500 text-white rounded-md"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default MessageSystem;
