import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Message {
  sender_mobile: string;
  receiver_mobile: string;
  message: string;
  time: string;
}

interface MessageSystemProps {
  chat: Chat | null;
}

interface Chat {
  name: string;
  avatar: string;
  status: string;
  mobile: string; 
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (chat) {
      // Fetch messages for the selected chat
      const fetchMessages = async () => {
        try {
          const response = await axios.get('http://localhost:5000/api/messages', {
            params: {
              sender_mobile: chat.mobile,
              receiver_mobile: 'OTHER_MOBILE' // Replace with actual receiver's mobile
            }
          });
          setMessages(response.data);
        } catch (error) {
          console.error('Error fetching messages:', error);
        }
      };

      fetchMessages();
    }
  }, [chat]);

  const handleSendMessage = async () => {
    if (chat) {
      try {
        await axios.post('http://localhost:5000/api/messages', {
          sender_mobile: chat.mobile,
          receiver_mobile: 'OTHER_MOBILE', // Replace with actual receiver's mobile
          message
        });
        setMessage('');
        // Refresh messages
        const response = await axios.get('http://localhost:5000/api/messages', {
          params: {
            sender_mobile: chat.mobile,
            receiver_mobile: 'OTHER_MOBILE' // Replace with actual receiver's mobile
          }
        });
        setMessages(response.data);
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  return (
    <div className="flex flex-col h-full">
      <div className="flex-1 overflow-y-auto">
        {messages.map((msg, index) => (
          <div key={index}>
            <div className={`p-2 ${msg.sender_mobile === chat?.mobile ? 'bg-blue-100' : 'bg-gray-100'}`}>
              <div>{msg.message}</div>
              <div className="text-xs">{msg.time}</div>
            </div>
          </div>
        ))}
      </div>
      <div className="flex p-2">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="flex-grow p-2 border rounded"
        />
        <button onClick={handleSendMessage} className="ml-2 p-2 bg-blue-500 text-white rounded">
          Send
        </button>
      </div>
    </div>
  );
};

export default MessageSystem;
