import React, { useState } from 'react';
import { FaSearch } from 'react-icons/fa'; // Import the search icon from react-icons

const Chats: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="flex flex-col bg-gray-100 p-5 rounded-lg mx-4 ml-56 h-80 w-[25%] shadow-sm" style={{ borderRightWidth: "1px" }}>
      {/* Search Bar */}
      <div className="relative mb-4 mt-3 ml-6">
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search..."
          className="w-full p-2 pl-10 pr-10 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
          <FaSearch className="w-6 h-6 text-gray-500" />
        </div>
      </div>

      <div className="left-3 ml-2 z-3 flex flex-col space-y-0">
        <div className="flex gap-2.5 font-work-sans">
          <div className="text-green-500">
            <button className="relative bg-none p-2.5 border-b-2 border-green-500 cursor-pointer text-green-500 text-xs">
              Recent Chats
            </button>
          </div>
          <button className="border-none bg-none p-2.5 cursor-pointer text-gray-400 text-xs">All Chats</button>
        </div>
        <div className="line text-gray-200">
          <hr className="m-0 w-[96%]" />
        </div>
      </div>

      <div className="bg-white p-4 border-2 h-16 mt-1 rounded mb-6">
        <div className='flex flex-row'>
          <img
            className="w-8 h-8 ml-2"
            src='/assets/vijay.svg'
            alt="Profile"
          />
          <div className='flex flex-col ml-1'>
            <div className='text-xs font-semibold'>Vijay</div>
            <div className='text-xs whitespace-nowrap'>James Added former jasmine</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chats;
