import React, { useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';

const socket = io('http://localhost:5000'); // Connect to WebSocket server

const AddButton: React.FC = () => {
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [mobileNumber, setMobileNumber] = useState('');
  const [name, setName] = useState('');

  const handleOpenPopup = () => {
    setIsPopupOpen(true);
  };

  const handleClosePopup = () => {
    setIsPopupOpen(false);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/AddUser', {
        mobileNumber,
        name
      });
      alert('User data saved successfully!');
      socket.emit('newUser', { mobileNumber, name }); // Emit event to WebSocket server
      handleClosePopup();
    } catch (error) {
      console.error('Error saving user data:', error);
      alert('Failed to save user data.');
    }
  };

  return (
    <div>
      <button
        className="bg-blue-500 text-white py-2 px-4 rounded"
        onClick={handleOpenPopup}
      >
        Add User
      </button>

      {isPopupOpen && (
        <div className="fixed inset-0 flex items-center justify-center">
          <div className="bg-white p-6 rounded shadow-lg">
            <h2 className="text-lg font-semibold">Add New User</h2>
            <form onSubmit={handleSubmit}>
              <div>
                <label className="block mb-2">Mobile Number:</label>
                <input
                  type="text"
                  value={mobileNumber}
                  onChange={(e) => setMobileNumber(e.target.value)}
                  className="border p-2 w-full"
                  required
                />
              </div>
              <div>
                <label className="block mb-2">Name:</label>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  className="border p-2 w-full"
                  required
                />
              </div>
              <div className="mt-4 flex gap-2">
                <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded">Add User</button>
                <button type="button" onClick={handleClosePopup} className="bg-gray-500 text-white py-2 px-4 rounded">Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AddButton;
