import React, { useState, useEffect } from 'react';
import { IoPersonCircleSharp } from "react-icons/io5";
import axios from 'axios';

interface Chat {
  name: string;
  mobileNumber: string;
  message: string;
  time: string;
  avatar: string;
  status: string;
}

interface MessageSystemProps {
  chat: Chat | null;
  loggedInUserMobileNumber: string;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat, loggedInUserMobileNumber }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Chat[]>([]);
  const [file, setFile] = useState<File | null>(null);
  const [user, setUser] = useState<{ name: string, status: string } | null>(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/user/${loggedInUserMobileNumber}`);
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUser();
  }, [loggedInUserMobileNumber]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleSendMessage = async () => {
    if (messageInput.trim() && user && chat) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage = {
        name: user.name,
        mobileNumber: loggedInUserMobileNumber,
        message: messageInput,
        time: formattedTime,
        avatar: '/assets/default-avatar.svg',
        status: user.status,
      };

      try {
        await axios.post('http://localhost:5000/api/messages', {
          sender_mobile: loggedInUserMobileNumber,
          receiver_mobile: chat.mobileNumber,
          sender_name: user.name,
          receiver_name: chat.name,
          message: messageInput,
        });

        setMessages([...messages, newMessage]);
        setMessageInput('');
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files ? e.target.files[0] : null;
    if (selectedFile && user && chat) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage = {
        name: user.name,
        mobileNumber: loggedInUserMobileNumber,
        message: `Sent a file: ${selectedFile.name}`,
        time: formattedTime,
        avatar: '/assets/default-avatar.svg',
        status: user.status,
      };

      const formData = new FormData();
      formData.append('file', selectedFile);
      formData.append('sender_mobile', loggedInUserMobileNumber);
      formData.append('receiver_mobile', chat.mobileNumber);
      formData.append('sender_name', user.name);
      formData.append('receiver_name', chat.name);
      formData.append('message', `Sent a file: ${selectedFile.name}`);

      try {
        await axios.post('http://localhost:5000/api/messages', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });

        setMessages([...messages, newMessage]);
        setFile(null);
      } catch (error) {
        console.error('Error sending file:', error);
      }
    }
  };

  if (!chat) {
    return (
      <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
        <div className="flex justify-between items-center mb-4">
          <div className="text-xl font-[600] mb-5 text-[#232323] flex items-center space-x-2">
            <span>Select a chat to see messages.</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-grow mb-4 flex flex-col h-[calc(100vh-5rem)]">
      {/* Header with chat information */}
      <div className="flex justify-between items-center mb-4">
        <div className="text-xl font-[700] text-[#232323] flex items-center space-x-2">
          <IoPersonCircleSharp className='w-14 h-14'/>
          <div>
            <span className='font-medium'>{chat.name}</span>
            <div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
              {chat.status}
            </div>
          </div>
        </div>
      </div>

      {/* Message area */}
      <div className="flex flex-col flex-grow overflow-y-auto bg-[#f6f6f8] p-4">
        {/* Display messages */}
        {messages.map((msg, index) => (
          <div key={index} className={`flex ${msg.mobileNumber === loggedInUserMobileNumber ? 'justify-end' : ''} mb-4`}>
            {msg.mobileNumber !== loggedInUserMobileNumber && <img src={msg.avatar} alt={msg.name} className="w-10 h-10 rounded-full mr-2" />}
            <div className={`flex flex-col ${msg.mobileNumber === loggedInUserMobileNumber ? 'items-end' : ''}`}>
              <p className="bg-white p-2 w-[490px] text-xs rounded-lg shadow-md">
                {msg.message}
                <span className="text-xs text-gray-500 float-right" style={{ position: 'relative', top: '-5px', marginRight: '10px' }}>
                  {msg.time}
                </span>
              </p>
            </div>
            {msg.mobileNumber === loggedInUserMobileNumber && <img src={msg.avatar} alt={msg.name} className="w-10 h-10 rounded-full ml-2" />}
          </div>
        ))}
      </div>

      {/* Input area */}
      <div className="flex items-center mb-4 p-4 border-t bg-[#ffffff] shadow-md">
        <input
          type="file"
          id="file-input"
          className="hidden"
          onChange={handleFileChange}
        />
        <label htmlFor="file-input">
          <img className="p-2 mr-2 bg-white w-10 h-10 cursor-pointer" src="/assets/add.svg" alt="Add" />
        </label>
        <input
          type="text"
          value={messageInput}
          onChange={handleInputChange}
          placeholder="Type Here"
          className="text-xs font-medium text-gray-400 flex-grow p-2 border rounded-lg outline-none" 
        />
        <img
          onClick={handleSendMessage}
          className="p-2 bg-white w-14 h-14 cursor-pointer"
          src="/assets/sendbutton.svg"
          alt="Send"
        />
      </div>
    </div>
  );
};

export default MessageSystem;
