// src/component/MessageSystem.tsx
import React, { useState } from 'react';

interface Chat {
  name: string;
  message: string;
  time: string;
  avatar: string;
  status: string;
}

interface MessageSystemProps {
  chat: Chat | null;
}

const MessageSystem: React.FC<MessageSystemProps> = ({ chat }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Chat[]>([]);
  const [file, setFile] = useState<File | null>(null);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessageInput(e.target.value);
  };

  const handleSendMessage = () => {
    if (messageInput.trim()) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage = {
        name: chat?.name || 'User',
        message: messageInput,
        time: formattedTime,
        avatar: chat?.avatar || '/assets/default-avatar.svg',
        status: chat?.status || 'offline',
      };

      setMessages([...messages, newMessage]);
      setMessageInput('');
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files ? e.target.files[0] : null;
    if (selectedFile) {
      const now = new Date();
      const options: Intl.DateTimeFormatOptions = {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      };
      const formattedTime = now.toLocaleTimeString([], options);

      const newMessage = {
        name: chat?.name || 'User',
        message: `Sent a file: ${selectedFile.name}`,
        time: formattedTime,
        avatar: chat?.avatar || '/assets/default-avatar.svg',
        status: chat?.status || 'offline',
      };

      setMessages([...messages, newMessage]);
      setFile(null);
    }
  };

  if (!chat) {
    return (
      <div className="flex-grow mb-4 p-4 ml-64 mt-20 flex flex-col h-[calc(100vh-5rem)]">
        <div className="flex justify-between items-center mb-4">
          <div className="text-xl font-bold mb-5 text-gray-800 flex items-center space-x-2">
            <span>Select a chat to see messages.</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-grow mb-4 flex flex-col h-[calc(100vh-5rem)]">
      {/* Header with chat information */}
      <div className="flex justify-between items-center mb-4">
        <div className="text-xl font-bold text-gray-800 flex items-center space-x-2">
          <img className="w-12 h-12 rounded-full" src={chat.avatar} alt={chat.name} />
          <div>
            <span>{chat.name}</span>
            <div className={`text-xs ${chat.status === 'Online' ? 'text-green-500' : 'text-gray-500'}`}>
              {chat.status}
            </div>
          </div>
        </div>
      </div>

      {/* Message area */}
      <div className="flex flex-col flex-grow overflow-y-auto bg-gray-100 p-4">
        {messages.map((msg, index) => (
          <div key={index} className={`flex ${msg.name === chat?.name ? 'justify-end' : ''} mb-4`}>
            {msg.name !== chat?.name && <img src={msg.avatar} alt={msg.name} className="w-10 h-10 rounded-full mr-2" />}
            <div className={`flex flex-col ${msg.name === chat?.name ? 'items-end' : ''}`}>
              <p className="bg-white p-2 w-[490px] text-xs rounded-lg shadow-md">
                {msg.message}
                <span className="text-xs text-gray-500 float-right" style={{ position: 'relative', top: '-5px', marginRight: '10px' }}>
                  {msg.time}
                </span>
              </p>
            </div>
            {msg.name === chat?.name && <img src={msg.avatar} alt={msg.name} className="w-10 h-10 rounded-full ml-2" />}
          </div>
        ))}
      </div>

      {/* Message input area */}
      <div className="flex items-center p-4 bg-gray-200">
        <input
          type="file"
          onChange={handleFileChange}
          className="mr-2"
        />
        <input
          type="text"
          value={messageInput}
          onChange={handleInputChange}
          placeholder="Type a message..."
          className="flex-grow p-2 text-xs border rounded-lg"
        />
        <button
          onClick={handleSendMessage}
          className="ml-2 bg-blue-500 text-white py-2 px-4 rounded"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default MessageSystem;
