import React, { useState } from 'react';

const Chats: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="flex flex-col bg-gray-100 p-5 rounded-lg mx-4 mt-2 ml-56 mr-50 h-80 w-[25%] shadow-sm" style={{ borderRightWidth: "1px" }}>
      {/* Search Bar */}
      <div className="mb-4 mt-3 ml-6">
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search..."
          className="w-[90%] p-1 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

       <div className="bg-white p-4 border-2 h-24 rounded mb-6">
        <div className='flex flex-row'>
        <img
          className="w-8 h-8 mt-2 ml-2"
          src='/assets/vijay.svg'
          alt="Profile"
        />
        <div className='flex flex-col ml-1'>
          <div className='text-sm font-medium mb-1'>Vijay</div>
          <div className='text-xs whitespace-nowrap'>James Added former jasmine</div>
        </div>
        <div className='text-xs ml-530'>Just now</div>
        </div>
      </div>
    </div>
  );
};

export default Chats;
