import React, { useState, useEffect } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import MessageSystem from './Message-System';


interface Chat {
  name: string;
  message: string;
  time: string;
  avatar: string;
}

const Chats: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [currentTime, setCurrentTime] = useState('');

  useEffect(() => {
    const now = new Date();
    const options: Intl.DateTimeFormatOptions = {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    };
    const formattedTime = now.toLocaleTimeString([], options);
    setCurrentTime(formattedTime);
  }, []);

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  const chatData: Chat[] = [
    { name: 'Vijay', message: 'James Added former jasmine', time: currentTime, avatar: '/assets/vijay.svg' },
    { name: 'Jasmine', message: 'James Added former jasmine', time: currentTime, avatar: '/assets/sethu.svg' },
    { name: 'James', message: 'James Added former jasmine', time: currentTime, avatar: '/assets/sathish.svg' },
    { name: 'Karthi', message: 'James Added former jasmine', time: currentTime, avatar: '/assets/shiek.svg' },
    { name: 'Shiek', message: 'James Added former jasmine', time: currentTime, avatar: '/assets/vijay.svg' }
  ];

  return (
    <>
    <div className="flex flex-col bg-white p-5 rounded-lg mx-4 ml-56 h-auto w-[28%] border-r-4 shadow-xl">
      {/* Search Bar */}
      <div className="flex justify-center mb-4 mt-3">
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search here"
          className="w-[85%] p-2 rounded-lg bg-gray-100 placeholder:text-xs text-left focus:outline-none focus:ring-1 focus:ring-blue-500"
        />
         <i className="fa fa-search absolute ml-56 mt-3 mr-3 text-gray-400"></i>
      </div>

      <div className="left-3 ml-2 z-3 flex flex-col space-y-0">
        <div className="flex gap-2.5 font-work-sans">
          <div className="text-green-500">
            <button className="relative bg-none p-2.5 border-b-2 border-green-500 cursor-pointer text-green-500 text-xs">
              Recent Chats
            </button>
          </div>
          <button className="border-none bg-none p-2.5 cursor-pointer text-gray-400 text-xs">All Chats</button>
        </div>
        <div className="line text-gray-200">
          <hr className="m-0 w-[93%]" />
        </div>
      </div>

      {chatData.map((chat, index) => (
        <div key={index}>
          <div className="bg-white p-4 h-16 mt-1 rounded mb-1">
            <div className='flex flex-row'>
              <img
                className="w-8 h-8 ml-2"
                src={chat.avatar}
                alt="Profile"
              />
              <div className='flex flex-col ml-1'>
                <div className='text-xs font-semibold' style={{color:"#232323"}}>{chat.name}</div>
                <div className='text-xs whitespace-nowrap'>{chat.message}</div>
              </div>
              <div className='text-xs ml-6'>{chat.time}</div>
            </div>
          </div>

          <div className="line text-gray-200">
            <hr className="m-0 b- w-[93%]" />
          </div>
        </div>
      ))}
    </div>
    <div className='flex-1'>
      <MessageSystem/>
    </div>
    </>

  );
};

export default Chats;
